import{_ as o,r as t,o as r,c as s,a as e,b as n,d as c}from"./app-91f40cf0.js";const i={},l=e("h2",{id:"白志东",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#白志东","aria-hidden":"true"},"#"),n(" 白志东")],-1),d=e("h3",{id:"发展中国家科学院院士",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#发展中国家科学院院士","aria-hidden":"true"},"#"),n(" 发展中国家科学院院士")],-1),_=e("p",null,"1943 年生，河北省乐亭人。1982 年毕业于中国科技大学数学系，获得博士学位。1984 年 9 月出国留学，先后在美国匹兹堡大学和宾州州立大学统计系担任研究员，美国天普大学统计系担任副教授，台湾中山大学应用数学系和新加坡国立大学概率统计系担任教授。1990 年 3 月被评为发展中国家科学院院士。自 2002 年 5 月回国以来，一直在东北师范大学数学与统计学院担任特聘教授。现担任中国概率统计学会常务理事，曾担任《Journal of Multivariate Analysis》主编，《Journal of statistical planning and inference》副主编，《Sankya》副主编。自 1982 年参加工作以来，一直从事概率统计中极限理论方面的研究。是一位多产的世界级数学家，至今已发表学术论文近 300 篇，另有 10 多篇应邀为各学术专著所写的章节。目前论文已为 SCI 引用达 3000 余次。已出版专著 5 部。主要研究领域包括:大维随机矩阵的谱分析理论，分布函数的渐进展开，模型选择，信号处理，M-估计，深度估计，临床试验中的序贯设计，算法中的应用概率等。其成果在例如原子物理，无线电电子学，经济，管理，金融，保险，数理统计等方面都有重要应用。2007 年获得教育部自然科学一等奖，2012 年获得国家自然科学奖二等奖。",-1),h={href:"https://book.eol.cn/books/kpeu/mobile/index.html",target:"_blank",rel:"noopener noreferrer"};function u(f,p){const a=t("ExternalLinkIcon");return r(),s("div",null,[l,d,_,e("blockquote",null,[e("p",null,[n("来源: 2023 年本科招生指南 "),e("a",h,[n("相关链接"),c(a)])])])])}const k=o(i,[["render",u],["__file","baizhidong.html.vue"]]);export{k as default};
