import{_ as p,r as n,o,c,a as i,d as l,w as t,b as a,e as d}from"./app-91f40cf0.js";const r={},h=d('<h2 id="专业简介" tabindex="-1"><a class="header-anchor" href="#专业简介" aria-hidden="true">#</a> 专业简介</h2><p>为普通类专业，新闻学专业旨在培养具备深厚文化知识底蕴，具备全面的新闻学理论和专业技能，从事报刊、广播电视及新媒体的新闻采编、制作、传播、媒介市场调查与分析以及党政机关、企事业单位的宣传策划等工作的高水平复合型新闻人才；广告学专业旨在培养具有高远的职业理想和良好的职业道德，具有自主创新精神和先进传播理念，具有系统的广告学专业知识、技能和技术素养，适应媒介融合环境下多元多样化传播的需要，具备宽广的知识视野和一定的独立从事专业研究的创新意识、能力，以及良好的团队合作意识与社会传播实践专业技能的复合型高层次专门人才。</p><p>新闻传播学类为普通类专业，有新闻学和广告学两个专业方向，入学一年半后根据学生成绩和意愿分专业。</p><h2 id="学费价格" tabindex="-1"><a class="header-anchor" href="#学费价格" aria-hidden="true">#</a> 学费价格</h2><p>新闻传播学类学费为 6600 元/年</p><h2 id="主干课程" tabindex="-1"><a class="header-anchor" href="#主干课程" aria-hidden="true">#</a> 主干课程</h2><ul><li><p>新闻理论</p></li><li><p>文学基础</p></li><li><p>艺术基础</p></li><li><p>传播学基础</p></li><li><p>新媒体概论</p></li><li><p>中外新闻史</p></li><li><p>新闻采访与写作</p></li><li><p>新闻编辑</p></li><li><p>新闻评论</p></li><li><p>广播电视新闻学</p></li><li><p>网络新闻学</p></li><li><p>媒介经营与管理</p></li><li><p>媒介市场调查与分析</p></li><li><p>广告学</p></li><li><p>广告策划与创意</p></li><li><p>广告媒体策略</p></li><li><p>品牌设计与传播</p></li><li><p>市场调查与分析</p></li><li><p>营销学和影视广告制作</p></li><li><p>创意传播</p></li><li><p>消费者行为学</p></li><li><p>广告文案写作</p></li></ul>',7),_={class:"innenu-list"},u=i("div",{class:"innenu-list-detail"},[i("div",{class:"innenu-list-text"}," 新闻学 ")],-1),m=i("div",{class:"innenu-list-detail"},[i("div",{class:"innenu-list-text"}," 广告学 ")],-1),f={href:"https://book.eol.cn/books/kpeu/mobile/index.html",target:"_blank",rel:"noopener noreferrer"};function x(k,b){const e=n("VPLink"),s=n("ExternalLinkIcon");return o(),c("div",null,[h,i("div",_,[l(e,{class:"innenu-list-item",to:"news.md"},{default:t(()=>[u]),_:1}),l(e,{class:"innenu-list-item",to:"ad.md"},{default:t(()=>[m]),_:1})]),i("blockquote",null,[i("p",null,[a("来源: 2022 本科招生指南、传媒科学学院官网 "),i("a",f,[a("相关链接"),l(s)])])])])}const V=p(r,[["render",x],["__file","class.html.vue"]]);export{V as default};
