import{_ as a,r as o,o as d,c as p,a as t,d as n,w as i,b as s,e as l}from"./app-91f40cf0.js";const _={},h=t("h2",{id:"寝室介绍",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#寝室介绍","aria-hidden":"true"},"#"),s(" 寝室介绍")],-1),r={class:"innenu-list"},u=t("img",{class:"innenu-list-icon",src:"https://mp.innenu.com/data/icon/dorm.svg","no-view":""},null,-1),m=t("div",{class:"innenu-list-detail"},[t("div",{class:"innenu-list-text"}," 寝室 ")],-1),v=t("div",{class:"hint-container info"},[t("p",{class:"hint-container-title"},"东师是几人间"),t("p",null,"本科生 6 人寝室。"),t("p",null,"研究生 4 人寝室，4 人寝室不足的，安排至 6 人寝。")],-1),f=t("div",{class:"hint-container info"},[t("p",{class:"hint-container-title"},"寝室都是上床下桌么?"),t("p",null,"本科生全部寝室均为上床下桌。"),t("p",null,"研究生绝大部分寝室都是上床下桌。受往年疫情扩招影响，部分男研究生寝室改为为 6 人间，为上下铺。(如本部七舍部分寝室)")],-1),g={class:"hint-container info"},x=t("p",{class:"hint-container-title"},"有寝室照片么?",-1),k=t("p",null,"有，寝室实拍照片请详见“寝室环境”。",-1),V=l('<div class="hint-container info"><p class="hint-container-title">寝室床多大?</p><p>床铺尺寸: 长 1.9m - 2m，宽 0.85m - 0.9m (研究生部分寝室床铺可能厂家不同尺寸有差异)</p><p>床铺外侧护栏，尺寸为: 长 120cm 高 24cm</p><p>注: 2m × 0.9m 是厂家给的数据，是算上了中间的木质床板 + 外围的金属框的。床板的尺寸为 1.9m × 0.85m。排除掉护栏，前后的蚊帐架后，实际可铺尺寸约为 1.94m × 0.86m (尺寸仅供参考)</p></div><div class="hint-container info"><p class="hint-container-title">是否需要自备床上用品?</p><p>寝室只提供一个军绿色的薄垫子，可以根据需求额外加一个床垫。开学两校区超市会以接近批发价售卖床上用品。 (数量有限卖完为止)</p><p>其他所有床上用品均需自备，包括枕头、被子、床单。学校对样式无要求，只要不是特别奇怪 (如黄色、暴力、恐怖、血腥) 就没人管。</p><p>注: 体育学院军事化管理统一发放床单、被罩、枕套。需要提前准备 200cm × 150cm 的被芯和 74cm × 48cm 的枕芯。</p></div><div class="hint-container info"><p class="hint-container-title">寝室有空调或者电扇么?</p><p>寝室无空调或电扇。寝室夜间断电，所以本科生如有需求，可以购买 USB 接口的小风扇接充电宝。研究生因寝室全天供电，可考虑电风扇或空调扇。</p><p>注: 由于东北气候较冷的地域性原因，东北大学寝室都没有空调，国家不给相关拨款。另外上床下桌的设计无法安装吊扇。</p></div>',3),B={class:"hint-container info"},L=t("p",{class:"hint-container-title"},"寝室有独卫么",-1),N=t("p",null,"没有，只有公共卫生间。有阿姨每天定期打扫 (一天几次)，非常整洁。",-1),b={class:"hint-container info"},w=t("p",{class:"hint-container-title"},"寝室有独立浴室么",-1),E=t("p",null,"没有，只有公共浴池。公共浴池有隔间，洗一次澡约 3 - 5 元。",-1),R=l('<div class="hint-container info"><p class="hint-container-title">寝室可以养宠物么?</p><p>不可以，规定上什么都不能养，但可以养花草。</p><p>如果实在想养，几条小金鱼是不管的。</p></div><div class="hint-container info"><p class="hint-container-title">寝室有蚊子么?如何驱蚊?</p><p>寝室有没有蚊子取决于寝室的地理位置，一般低楼层、窗外有植被的蚊子会多一些，反之少一些。</p><p>所有寝室窗户均安装了纱窗，但是蚊子可能通过大门、部分有缝的窗户进来，并经楼梯间或电梯进入到各个楼层。</p><p>学生寝室是严禁点蚊香的，发现者会受到处分。</p><p>由于夜间断电，常规的电蚊香液也是无法使用的。</p><p>可以考虑蚊帐或花露水。</p></div><div class="hint-container info"><p class="hint-container-title">寝室可以悬挂蚊帐么?</p><p>夜间是可以悬挂的，但保卫处防火科不让白天悬挂，而且有的辅导员的确会管。</p><p>可以考虑买四个大一点的挂钩粘在天花板的四个角上，钩超外。这样每天晚上就可以掏出蚊帐挂上，早上可以很方便的摘下来。</p></div>',3),C={class:"hint-container info"},P=t("p",{class:"hint-container-title"},"寝室可以悬挂床帘么?",-1),S=t("p",null,"不能。",-1),A=t("p",null,"《东北师范大学学生公寓管理办法》『第六章 卫生管理』第三十七条 (三): 起床后要叠被、整理床铺，个人物品摆放整齐，禁止悬挂床帘、吊椅；",-1);function D(M,T){const c=o("VPLink"),e=o("RouterLink");return d(),p("div",null,[h,t("div",r,[n(c,{class:"innenu-list-item",to:"/guide/dorm/README.md"},{default:i(()=>[u,m]),_:1})]),v,f,t("div",g,[x,k,t("ul",null,[t("li",null,[n(e,{to:"/guide/dorm/detail/env.html"},{default:i(()=>[s("查看详情")]),_:1})])])]),V,t("div",B,[L,N,t("ul",null,[t("li",null,[n(e,{to:"/guide/dorm/detail/toilet.html"},{default:i(()=>[s("查看详情")]),_:1})])])]),t("div",b,[w,E,t("ul",null,[t("li",null,[n(e,{to:"/guide/bath/"},{default:i(()=>[s("查看详情")]),_:1})])])]),R,t("div",C,[P,S,A,t("ul",null,[t("li",null,[n(e,{to:"/intro/rule/dorm-manage.html"},{default:i(()=>[s("查看详情")]),_:1})])])])])}const j=a(_,[["render",D],["__file","dorm.html.vue"]]);export{j as default};
