import{_ as a,r as c,o as r,c as i,a as e,b as t,d as o,e as s}from"./app-91f40cf0.js";const d={},h=e("h2",{id:"计分方式",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#计分方式","aria-hidden":"true"},"#"),t(" 计分方式")],-1),l=e("p",null,"NCRE 考试实行百分制计分，以等第成绩通知考生。等第成绩分为不及格、及格、良好、优秀四等。0-59 分为不及格，60-79 分为及格，80-89 分为良好，90-100 分为优秀。考试成绩在及格以上者，由教育部考试中心印制合格证书。考试科目成绩为及格的，合格证书上标注“合格”字样；考试成绩为良好的，合格证书上标注“良好”字样；考试成绩为优秀的，合格证书上标注“优秀”字样。照片不符合规定的考生，以予打印证书。NCRE 所有级别证书均无时效限制。",-1),_=e("h2",{id:"成绩查询",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#成绩查询","aria-hidden":"true"},"#"),t(" 成绩查询")],-1),p=e("p",null,"考试成绩约在考试结束 2 个月后公布，考生可登录教育部考试中心中国教育考试网查询本次考试成绩及证书信息。",-1),f={href:"https://cjcx.neea.edu.cn/html1/folder/22014/5476-1.htm",target:"_blank",rel:"noopener noreferrer"},u=s('<h2 id="证书获得条件" tabindex="-1"><a class="header-anchor" href="#证书获得条件" aria-hidden="true">#</a> 证书获得条件</h2><p>考生参加考试获得证书的条件必须符合《全国计算机等级考试证书体系》的规定。具体规定如下: 二级 MS Office 高级应用与设计 (科目代码 65) 和三级所有科目获证条件为该科目成绩合格，即可获得相应科目证书；二级科目 (除 MS Office 高级应用与设计外的其他二级科目) 获证条件为: 科目总分达到 60 分且选择题得分达到选择题总分 50%及以上，方可获得合格证书；四级科目获证条件为四级科目成绩合格，并已经 (或同时) 获得相应三级科目证书。</p><div class="hint-container info"><p class="hint-container-title">电子证书</p><p>自 2022 年起，考试中心推出 NCRE 电子证书，所有符合取证条件的考生都将获得电子证书。2022 年为试点期，考生在报名时可同时申请纸质证书。</p></div><div class="hint-container info"><p class="hint-container-title">证书直邮服务</p><p>考生在考后可登录中国教育考试网申请 NCRE 证书直邮服务，有关要求和申请流程详见网站。</p></div><div class="hint-container warning"><p class="hint-container-title">四级证书限制</p><p>四级科目的合格成绩，自考试结束之日起可保留半年 (按月计算) 。</p></div>',5);function m(x,v){const n=c("ExternalLinkIcon");return r(),i("div",null,[h,l,_,p,e("p",null,[e("a",f,[t("https://cjcx.neea.edu.cn/html1/folder/22014/5476-1.htm"),o(n)])]),u])}const E=a(d,[["render",m],["__file","grade.html.vue"]]);export{E as default};
