import{_ as o,r as t,o as r,c as s,a as e,b as n,d as i,e as l}from"./app-91f40cf0.js";const c={},d=l('<h2 id="个人补办公费医疗手册的办理流程及说明" tabindex="-1"><a class="header-anchor" href="#个人补办公费医疗手册的办理流程及说明" aria-hidden="true">#</a> 个人补办公费医疗手册的办理流程及说明</h2><p>一、办理对象: 学制期内的所有招收的计划内本科生、研究生(定向委培生除外)及少数民族骨干计划学生。</p><p>二、办理所需材料: 近期免冠一寸照片一张，医疗手册工本费 4.5 元/人，填写好的“补办《学生医疗手册》申请表”。</p><p>三、办理时间: 正常工作日周一至周五 AM 8:30-11:00、PM 14:00-16:00 (法定节假日除外)。</p><p>四、办理流程:</p><ol><li>下载“补办《学生医疗手册》申请表”，自行打印。</li><li>认真填写“补办《学生医疗手册》申请表”，并黏贴好照片，到所在院系找负责人签字并加盖院系公章。</li><li>携带已填写好的申请表及个人校园一卡通到校医院一楼挂号室(本部医院或净月卫生所)缴费办理。</li><li>得到公费医疗手册后请将申请表的正联自行黏贴于手册的个人信息页上，以便日后使用。</li></ol><h3 id="备注" tabindex="-1"><a class="header-anchor" href="#备注" aria-hidden="true">#</a> 备注</h3><ul><li>此申请表仅用于个人补办学生医疗手册使用。 申请人需认真填写申请表并黏贴好照片，由院系负责人签字并加盖学院公章后方可生效。 学生医疗手册仅限本人使用，使用过程中需同时出示校园一卡通。</li></ul><h2 id="相关文件" tabindex="-1"><a class="header-anchor" href="#相关文件" aria-hidden="true">#</a> 相关文件</h2><a class="innenu-doc" href="https://assets.innenu.com/file/hospital/quanri.pdf" name="补办《学生医疗手册》申请表(全日制).pdf" target="_blank" rel="noopener noreferrer"><img class="innenu-doc-icon" src="https://mp.innenu.com/data/icon/pdf.svg" alt="补办《学生医疗手册》申请表(全日制)"> 补办《学生医疗手册》申请表(全日制).pdf </a><a class="innenu-doc" href="https://assets.innenu.com/file/hospital/shaomin.pdf" name="补办《学生医疗手册》申请表(少民计划制).pdf" target="_blank" rel="noopener noreferrer"><img class="innenu-doc-icon" src="https://mp.innenu.com/data/icon/pdf.svg" alt="补办《学生医疗手册》申请表(少民计划制)"> 补办《学生医疗手册》申请表(少民计划制).pdf </a>',11),h=e("p",null,"信息来源: 校医院官网",-1),p={href:"https://hos.nenu.edu.cn/info/1095/1123.htm",target:"_blank",rel:"noopener noreferrer"};function f(u,_){const a=t("ExternalLinkIcon");return r(),s("div",null,[d,e("blockquote",null,[h,e("p",null,[n("发布日期: 2017-10-19 "),e("a",p,[n("相关链接"),i(a)])])])])}const b=o(c,[["render",f],["__file","handbook.html.vue"]]);export{b as default};
