import{_ as i,r as c,o,c as s,a as n,b as e,d as a,e as l}from"./app-91f40cf0.js";const u={},r=n("div",{class:"innenu-account"},[n("img",{class:"innenu-account-background",src:"https://assets.innenu.com/img/media/foreign.jpg",alt:"外国语学院",loading:"lazy","no-view":""}),n("div",{class:"innenu-account-content"},[n("img",{class:"innenu-account-logo",src:"https://assets.innenu.com/img/media/foreign.jpg",alt:"外国语学院",loading:"lazy","no-view":""}),n("div",{class:"innenu-account-name"},"外国语学院"),n("div",{class:"innenu-account-detail"},"东北师范大学外国语学院")]),n("div",{class:"innenu-account-action-list"},[n("button",{class:"innenu-account-action","data-wxid":"nenuwgy"},[n("img",{class:"innenu-account-icon",src:"https://mp.innenu.com/data/icon/wechat.svg","no-view":""})]),n("a",{class:"innenu-account-action",href:"https://wy.nenu.edu.cn/",target:"_blank"},[n("img",{class:"innenu-account-icon",src:"https://mp.innenu.com/data/icon/web.svg","no-view":""})]),n("a",{class:"innenu-account-action",href:"mailto:wy@nenu.edu.cn"},[n("img",{class:"innenu-account-icon",src:"https://mp.innenu.com/data/icon/mail.svg","no-view":""})])])],-1),d=l('<h2 id="学院简介" tabindex="-1"><a class="header-anchor" href="#学院简介" aria-hidden="true">#</a> 学院简介</h2><p>外国语学院前身外语系始建于 1948 年。学院设有英语系、日语系、俄语系、商务英语系、德语系、大学外语系及第二外语教研室七个教学单位。现有外国语言文学一级学科博士授予权及博士后科研流动站；具有英语语言文学、日语语言文学、俄语语言文学、外国语言学及应用语言学、比较文学与跨文化研究 5 个二级学科博士点，具有英语语言文学、日语语言文学、俄语语言文学、外国语言学及应用语言学、外语教育和翻译专业硕士六个二级学科硕士点。科研与教学实力在东北领先，在国内居于前列。</p><h2 id="学科建设" tabindex="-1"><a class="header-anchor" href="#学科建设" aria-hidden="true">#</a> 学科建设</h2><p>学院始终坚持“教学与科研并重、理论与实践并重、外语素质与人文素质并重”的教育教学理念。经过多年的建设和发展，现有教授 27 人，副教授 55 人。学院曾获得国家级教学成果二等奖 1 项，省级教学成果奖 4 项；建设省级精品课 5 门；多位教师获得吉林省教育科学优秀成果奖和出版国家级规划教材。英语专业于 2007 年被评为首批国家特色专业；于 2019 年获批国家级一流本科专业建设点，并已通过教育部师范类专业二级认证；日语、俄语专业于 2020 年获批国家级一流本科专业建设点，商务英语专业于 2021 年获批国家级一流本科专业建设点，日语专业已通过教育部师范类专业二级认证，俄语专业完成教育部师范类专业二级认证流程。学院重视外国语言文学理论与应用研究，关注基础外语教育研究，现设有中国基础外语教育研究中心，并取得了一批高水平教学科研成果。东北师范大学外国语言文学学科在 2018-2022 的五年间，获批各类项目 113 项，其中国家社科项目 19 项 (包括重大项目 2 项，重点项目 1 项，后期资助项目 1 项、全国教育科学规划项目 1 项)、教育部人文社科项目 19 项，到账科研经费 1040 万元。近五年来发表学术论文 556 篇，其中 SSCI 论文 74 篇，A&amp;HCI6 篇，CSSCI 论文 212 篇；出版学术著作 48 部；获批咨询报告 3 项；各类科研获奖 18 项。</p><h2 id="人才培养" tabindex="-1"><a class="header-anchor" href="#人才培养" aria-hidden="true">#</a> 人才培养</h2><p>学院人才培养特色鲜明。学院就业率一直位居全校前列。学院人才培养专业素质过硬，在全国各类外语专业大赛中取得优异成绩。目前，学院人才培养已形成“东师外院”品牌，受到用人单位广泛青睐。学院注重培养学生国际视野，多名学生在美国、英国、加拿大、澳大利亚、新西兰、荷兰、爱尔兰、俄罗斯、日本、新加坡等国家和香港、澳门、台湾地区的大学进行交流学习或攻读硕博士学位。本科招生专业包括:英语、英语 (公费师范)、英语 (优师专项)、日语、俄语、商务英语、德语、英语(中外合作办学)。</p><h2 id="联系方式" tabindex="-1"><a class="header-anchor" href="#联系方式" aria-hidden="true">#</a> 联系方式</h2><ul><li><p>地址: 吉林省长春市人民大街 5268 号 | 吉林省长春市净月经济开发区净月大街 2555 号</p></li><li><p>邮政编码: 130024 | 130117</p></li><li><p>邮箱: wy@nenu.edu.cn</p></li><li><p>电话: (86-431)-85099679</p></li></ul>',8),h={class:"hint-container info"},_=n("p",{class:"hint-container-title"},"外国语学院 联系方式",-1),p=n("li",null,"姓名: 外国语学院",-1),m=n("li",null,[e("电话: "),n("a",{href:"tel:0431-85099679"},"0431-85099679")],-1),f=n("li",null,"公司电话: 0431-85099679",-1),g={href:"https://wy.nenu.edu.cn/",target:"_blank",rel:"noopener noreferrer"},v=n("li",null,[e("邮箱: "),n("a",{href:"mailto:wy@nenu.edu.cn"},"wy@nenu.edu.cn")],-1),w=n("li",null,"组织: 东北师范大学",-1),b=n("li",null,"地址: 吉林省长春市人民大街 5268 号",-1),k=n("li",null,"邮编: 130024(本部) / 130117(净月)",-1),x={href:"https://book.eol.cn/books/kpeu/mobile/index.html",target:"_blank",rel:"noopener noreferrer"};function y(C,I){const t=c("ExternalLinkIcon");return o(),s("div",null,[r,d,n("div",h,[_,n("ul",null,[p,m,f,n("li",null,[e("网站: "),n("a",g,[e("https://wy.nenu.edu.cn/"),a(t)])]),v,w,b,k])]),n("blockquote",null,[n("p",null,[e("来源: 2023 年本科招生指南 "),n("a",x,[e("相关链接"),a(t)])])])])}const N=i(u,[["render",y],["__file","intro.html.vue"]]);export{N as default};
