import{_ as i,r as c,o,c as s,a as n,b as e,d as t,e as l}from"./app-91f40cf0.js";const r={},u=n("div",{class:"innenu-account"},[n("img",{class:"innenu-account-background",src:"https://assets.innenu.com/img/media/media.png",alt:"传媒科学学院",loading:"lazy","no-view":""}),n("div",{class:"innenu-account-content"},[n("img",{class:"innenu-account-logo",src:"https://assets.innenu.com/img/media/media.png",alt:"传媒科学学院",loading:"lazy","no-view":""}),n("div",{class:"innenu-account-name"},"传媒科学学院"),n("div",{class:"innenu-account-detail"},"东师传媒")]),n("div",{class:"innenu-account-action-list"},[n("button",{class:"innenu-account-action","data-wxid":"nenu_media"},[n("img",{class:"innenu-account-icon",src:"https://mp.innenu.com/data/icon/wechat.svg","no-view":""})]),n("a",{class:"innenu-account-action",href:"https://chuanmei.nenu.edu.cn/",target:"_blank"},[n("img",{class:"innenu-account-icon",src:"https://mp.innenu.com/data/icon/web.svg","no-view":""})]),n("a",{class:"innenu-account-action",href:"mailto:chuanmei@nenu.edu.cn"},[n("img",{class:"innenu-account-icon",src:"https://mp.innenu.com/data/icon/mail.svg","no-view":""})])])],-1),h=l('<h2 id="学院介绍" tabindex="-1"><a class="header-anchor" href="#学院介绍" aria-hidden="true">#</a> 学院介绍</h2><p>东北师范大学传媒科学学院 (新闻学院) 成立于 2000 年。</p><h2 id="师资队伍" tabindex="-1"><a class="header-anchor" href="#师资队伍" aria-hidden="true">#</a> 师资队伍</h2><p>学院现有教职工 66 人，其中博士生导师 7 人，教授、副教授 33 人，高级工程师 1 人，在校本科生 700 余人，硕士研究生 300 余人。</p><h2 id="学科建设" tabindex="-1"><a class="header-anchor" href="#学科建设" aria-hidden="true">#</a> 学科建设</h2><p>学院现设有广播电视编导、播音与主持艺术、新闻学、广告学、数字媒体技术等 5 个本科专业(系)；其中广播电视编导、新闻学、广告学、数字媒体技术为国家级一流本科专业，播音与主持艺术为省级一流本科专业。有戏剧与影视学、新闻传播学和艺术学理论等 3 个一级学科硕士点，广播电视、新闻与传播等 2 个专业学位硕士点；建有 1 个省级重点优势学科，2 个省级品牌特色专业，1 个省级人才培养模式创新实验区，1 个省级人文社科重点研究基地，1 个省级“重大需求协同创新中心”，建有现代教育技术研究所、视听文化研究中心、纪录片研究所等十余个专业研究机构。</p><h2 id="教学条件" tabindex="-1"><a class="header-anchor" href="#教学条件" aria-hidden="true">#</a> 教学条件</h2><p>学院示范中心是东北、内蒙地区首家国家级传媒实验教学示范中心，拥有 5000 多万元的各种视听与传播实验设备，实验室面积达 3400 多平方米，形成了集广播电视、新闻传播、播音主持、影视艺术、信息管理、数字技术、网络应用、平面设计、广告开发于一体的综合实验教学平台，具备完整的数字化电视节目制作体系、网络化的教育信息处理体系。</p><h2 id="人才培养" tabindex="-1"><a class="header-anchor" href="#人才培养" aria-hidden="true">#</a> 人才培养</h2><p>学院与国内外多所高校有着长期友好合作关系，交流覆盖美国、韩国、日本等多个国家，迄今参加交流的学生人数已逾百人。与国内外广播电影电视业、新闻业、广告业等保持良好合作关系，拥有 30 余名高水平行业兼职教师和 20 余个视听传媒实践基地。</p><p>长期以来，学院以“国内一流、国际有一定影响力的传媒人才培养基地和东北亚传媒高端智库”为发展总体定位，贯彻“传艺送道、媒文成章”的办学理念，秉承“特色化”“精英式”“全媒型”的教育教学观念，强化“素养全、基础实、专业强、应用快”的人才培养思路，坚持“影视与传播并重，艺术与技术共强”的发展路径，以“东北亚影视艺术与传播文化”为特色方向，培养出了一大批符合“美的传媒人”人才目标的高水平复合型传媒人才。</p><h2 id="联系方式" tabindex="-1"><a class="header-anchor" href="#联系方式" aria-hidden="true">#</a> 联系方式</h2><ul><li><p>地址: 长春市净月大街 2555 号</p></li><li><p>邮政编码: 130117</p></li><li><p>电话: 0431-84536480</p></li><li><p>邮箱: chuanmei@nenu.edu.cn</p></li></ul>',13),d={class:"hint-container info"},p=n("p",{class:"hint-container-title"},"传媒科学学院 联系方式",-1),_=n("li",null,"姓名: 传媒科学学院",-1),m=n("li",null,[e("电话: "),n("a",{href:"tel:0431-84536480"},"0431-84536480")],-1),f={href:"https://chuanmei.nenu.edu.cn/",target:"_blank",rel:"noopener noreferrer"},g=n("li",null,[e("邮箱: "),n("a",{href:"mailto:chuanmei@nenu.edu.cn"},"chuanmei@nenu.edu.cn")],-1),b=n("li",null,"组织: 东北师范大学",-1),v=n("li",null,"地址: 长春市净月大街 2555 号",-1),x=n("li",null,"邮编: 130117",-1),k=n("p",null,"来源: 2022 本科招生指南、传媒科学学院官网 相关链接:",-1),w={href:"https://book.eol.cn/books/kpeu/mobile/index.html",target:"_blank",rel:"noopener noreferrer"},y={href:"https://chuanmei.nenu.edu.cn/xygk/xyjj.htm",target:"_blank",rel:"noopener noreferrer"};function N(V,B){const a=c("ExternalLinkIcon");return o(),s("div",null,[u,h,n("div",d,[p,n("ul",null,[_,m,n("li",null,[e("网站: "),n("a",f,[e("https://chuanmei.nenu.edu.cn/"),t(a)])]),g,b,v,x])]),n("blockquote",null,[k,n("p",null,[n("a",w,[e("相关链接1"),t(a)])]),n("p",null,[n("a",y,[e("相关链接2"),t(a)])])])])}const j=i(r,[["render",N],["__file","intro.html.vue"]]);export{j as default};
