import{_ as i,r as c,o,c as s,a as n,b as e,d as t,e as l}from"./app-91f40cf0.js";const r={},u=n("div",{class:"innenu-account"},[n("img",{class:"innenu-account-background",src:"https://assets.innenu.com/img/media/biology.png",alt:"生命科学学院",loading:"lazy","no-view":""}),n("div",{class:"innenu-account-content"},[n("img",{class:"innenu-account-logo",src:"https://assets.innenu.com/img/media/biology.png",alt:"生命科学学院",loading:"lazy","no-view":""}),n("div",{class:"innenu-account-name"},"生命科学学院"),n("div",{class:"innenu-account-detail"},"东北师范大学生命科学学院")]),n("div",{class:"innenu-account-action-list"},[n("button",{class:"innenu-account-action","data-wxid":"skyxxxczx2015"},[n("img",{class:"innenu-account-icon",src:"https://mp.innenu.com/data/icon/wechat.svg","no-view":""})]),n("a",{class:"innenu-account-action",href:"https://sky.nenu.edu.cn/",target:"_blank"},[n("img",{class:"innenu-account-icon",src:"https://mp.innenu.com/data/icon/web.svg","no-view":""})]),n("a",{class:"innenu-account-action",href:"mailto:sky@nenu.edu.cn"},[n("img",{class:"innenu-account-icon",src:"https://mp.innenu.com/data/icon/mail.svg","no-view":""})])])],-1),d=l('<h2 id="学院简介" tabindex="-1"><a class="header-anchor" href="#学院简介" aria-hidden="true">#</a> 学院简介</h2><p>生命科学学院的前身是原自然科学院博物系，始建于 1948 年，1994 年撤系建院。经过 70 多年的建设，学院已发展成为国内一流、国际有影响的生物学人才培养和科学研究基地。</p><h2 id="学科建设" tabindex="-1"><a class="header-anchor" href="#学科建设" aria-hidden="true">#</a> 学科建设</h2><p>学院为一级学科博士学位授权单位，拥有“生物学”、“生态学”和“草学”3 个一级学科，均设有博士后科研流动站，招收 11 个二级学科方向的博士和硕士研究生。在教育部多轮学科评估中，生物学、生态学两个学科均位列 A 类学科，被列入学校四个国家一流学科重点培育学科以及吉林省特色高水平学科；草学学科入选吉林省特色高水平学科“优势特色学科”。</p><p>学院设有生物科学和生物技术两个本科专业，全部获评国家级一流本科专业建设点。两个专业实行“生物科学大类”招生，学生在大学二年级进行专业选择。生物科学专业下设生物科学 (公费师范)、生物科学 (优师专项) 和生物科学三个方向。生物科学专业通过了教育部师范类专业第三级认证，为全国首个通过师范类专业第三级认证的生物科学专业。</p><h2 id="师资力量" tabindex="-1"><a class="header-anchor" href="#师资力量" aria-hidden="true">#</a> 师资力量</h2><p>学院现有教职工 152 人，其中专任教师 106 人，包括教授 43 人，副教授 33 人，博士生导师 45 人。其中，5 位教师先后入选国务院学位委员会学科评议组成员，5 位教师入选 2018-2022 年教育部高等学校教学指导委员会，教师获评国家级人才称号 23 个，14 位教师获评省级人才或专家称号。</p><h2 id="人才培养" tabindex="-1"><a class="header-anchor" href="#人才培养" aria-hidden="true">#</a> 人才培养</h2><p>1996 年学院获教育部批准建立东北地区第一个“国家生物学基础科学研究和教学人才培养基地”，并于 2021 年入选教育部基础学科拔尖学生培养计划 2.0，实行学士、硕士、博士择优推荐、连续培养的培养模式。</p><p>学院现有国家级人才培养与科学研究平台 4 个 (国家级生物基础实验教学示范中心，生物学国家级虚拟仿真实验教学中心，药物基因和蛋白筛选国家工程实验室，国家科技创新基地吉林松嫩草地生态系统野外科学观测研究站) ，1 个国际科技合作基地 (国家人类干细胞库国际联合研究中心)，2 个教育部重点实验室(分子表观遗传学教育部重点实验室、植被生态科学教育部重点实验室) ，2 个教育部工程研究中心 (糖复合物教育部工程研究中心，教育部农业基因工程研究中心) ，2 个教育部创新团队，以及 8 个省级重点实验室和工程中心。</p><p>学院每年招收本科生 140 余人、硕士研究生 150 余人、博士研究生 50 余人、留学生 40 余人。</p><h2 id="联系方式" tabindex="-1"><a class="header-anchor" href="#联系方式" aria-hidden="true">#</a> 联系方式</h2><ul><li><p>地址: 吉林省长春市人民大街 5268 号</p></li><li><p>邮政编码: 130024</p></li><li><p>邮箱: sky@nenu.edu.cn</p></li><li><p>电话: 0431-85099590</p></li><li><p>传真: 0431-85693965</p></li></ul>',13),h={class:"hint-container info"},p=n("p",{class:"hint-container-title"},"生命科学学院 联系方式",-1),_=n("li",null,"姓名: 生命科学学院",-1),m=n("li",null,[e("电话: "),n("a",{href:"tel:0431-85099590"},"0431-85099590")],-1),f=n("li",null,"公司电话: 0431-85693965",-1),g={href:"https://sky.nenu.edu.cn/",target:"_blank",rel:"noopener noreferrer"},k=n("li",null,[e("邮箱: "),n("a",{href:"mailto:sky@nenu.edu.cn"},"sky@nenu.edu.cn")],-1),b=n("li",null,"组织: 东北师范大学",-1),v=n("li",null,"地址: 吉林省长春市人民大街 5268 号",-1),x=n("li",null,"邮编: 130024",-1),y={href:"https://book.eol.cn/books/kpeu/mobile/index.html",target:"_blank",rel:"noopener noreferrer"};function w(N,V){const a=c("ExternalLinkIcon");return o(),s("div",null,[u,d,n("div",h,[p,n("ul",null,[_,m,f,n("li",null,[e("网站: "),n("a",g,[e("https://sky.nenu.edu.cn/"),t(a)])]),k,b,v,x])]),n("blockquote",null,[n("p",null,[e("来源: 2022 本科招生指南 "),n("a",y,[e("相关链接"),t(a)])])])])}const B=i(r,[["render",w],["__file","intro.html.vue"]]);export{B as default};
