import{_ as n,o as a,c as e,a as i,e as t}from"./app-91f40cf0.js";const c={},s=i("div",{class:"innenu-account"},[i("img",{class:"innenu-account-background",src:"https://assets.innenu.com/img/association/joinin/logo.png",alt:"卓音网络工作室",loading:"lazy","no-view":""}),i("div",{class:"innenu-account-content"},[i("img",{class:"innenu-account-logo",src:"https://assets.innenu.com/img/association/joinin/logo.png",alt:"卓音网络工作室",loading:"lazy","no-view":""}),i("div",{class:"innenu-account-name"},"卓音网络工作室"),i("div",{class:"innenu-account-description"},"卓尔不群，音你不凡")]),i("div",{class:"innenu-account-action-list"})],-1),l=t('<h3 id="社团概况" tabindex="-1"><a class="header-anchor" href="#社团概况" aria-hidden="true">#</a> 社团概况</h3><p>成立时间: 2000 年 3 月 25 日</p><h2 id="工作室简介" tabindex="-1"><a class="header-anchor" href="#工作室简介" aria-hidden="true">#</a> 工作室简介</h2><p>卓音工作室附属于东北师范大学学生就业指导服务中心信息服务室。是由若干在校本科.生组成的一个非营利性组织。工作室成立于2000年3月25日，办公室位于东北师范大学本部校区行政楼 425 室。工作室占地一百余平米，分为休息区、工作区、会议区、教师间、储物间、服务器间。拥有九台一体机，十八台高配置台式机等设备。</p><h2 id="部门介绍" tabindex="-1"><a class="header-anchor" href="#部门介绍" aria-hidden="true">#</a> 部门介绍</h2><p>卓音工作室下设两个部门：产品部、设计部</p><div class="hint-container info"><p class="hint-container-title">产品部</p><p>微信推送、维持工作室的正常运营、策划和组织会议、培训及其他活动、管理固定资产和日常开销。</p></div><div class="hint-container info"><p class="hint-container-title">设计部</p><p>页面设计，更新卓音在线、设计海报、易拉宝、PPT 等视频剪辑、照片后期。</p></div><div style="text-align:center;"><p>工作室工作条件</p></div><h2 id="工作室成果展示" tabindex="-1"><a class="header-anchor" href="#工作室成果展示" aria-hidden="true">#</a> 工作室成果展示</h2><p>1999 年，建设第一版高校就业网开始，卓音工作室锐意进取、不断推陈出新，助推了就业信息服务多次变革。2000 年，我们推出了全国首家学历认证服务。2001 年，在全国首次推出 E-mail 就业信息定制。2002 年，建设东北高师就业联盟网。2003 年，实现手机短信定制就业信息。2005 年，推出“师范类就业信息搜索引擎”。2006 年，开通全国首家移动就业网。2007 年，建成 RSS 定制就业信息系统。2009 年起，建设“全国教育系统高校毕业生网上招聘周”专题网站。2010 年，推出视频就业服务。2013 年，开发了“东师微就业”智能化微信公共平台和手机应用软件“东师求职宝”。</p><h2 id="曾获奖项" tabindex="-1"><a class="header-anchor" href="#曾获奖项" aria-hidden="true">#</a> 曾获奖项</h2><ul><li><p>学校十佳网站(2008)</p></li><li><p>东北师范大学信息服务特色网站(2009)</p></li><li><p>东北师范大学优秀网站(2011)</p></li><li><p>第五届工商银行杯全国大学生金融创意大赛学校赛区暨优秀组织奖(2014)</p></li><li><p>东北师范大学首届“华兴科技杯”网页设计大赛冠军&#39;</p></li><li><p>芙蓉学子大型公益活动榜样力量优秀大学生奖(2013)</p></li><li><p>东北高师就业联盟网</p></li><li><p>东北师范大学就业指导服务中心官方网站</p></li><li><p>卓音在线 (卓音工作室官方网站)</p></li><li><p>专题页面、平面设计</p></li><li><p>援外作品</p></li></ul><h2 id="卓音工作室口号" tabindex="-1"><a class="header-anchor" href="#卓音工作室口号" aria-hidden="true">#</a> 卓音工作室口号</h2><p>卓尔不群，音你不凡</p>',15),o=[s,l];function d(r,p){return a(),e("div",null,o)}const u=n(c,[["render",d],["__file","joinin.html.vue"]]);export{u as default};
