import{_ as t,o as a,c as o,a as e,b as n}from"./app-91f40cf0.js";const c={},s=e("h2",{id:"专业简介",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#专业简介","aria-hidden":"true"},"#"),n(" 专业简介")],-1),_=e("p",null,"1956 年，东北师范大学中文系成立语言学理论教研室成立以来，宋振华、王今铮、刘伶、冯公达、胡晓研几位老先生引领东北师范大学文学院语言学专业的发展，并在国内语言学界产生一定影响。该专业旨在培养具有坚实的语言学及应用语言学专业知识；具有严谨的学风和独立从事语言学理论及应用方面研究工作能力的专门人才。该专业多年来培养了数十名博士、数百名硕士研究生，毕业生大多活跃在全国大专院校、科研机构以及基础教育领域，成为科研、教学的骨干力量。2006 年开始，语言学及应用语言学二级学科硕士学位授予点成立，下设语言理论研究、语言应用研究、对外汉语教学研究三个研究方向。2010 年改为语言理论研究、语言应用研究两个研究方向。该专业本科生课程主要有语言学概论、语用学导论、西方语言学流派、理论语言学、语言学文献阅读与学术动态、教师口语实训等。研究生课程主要有语言学史、理论语言学、认知语言学、篇章语言学、应用语言学、语义语用学、句法语义学、语言学研究方法论、对外汉语教学研究、语法理论等。现有在职教师 4 人，其中教授 1 人，博士生导师 1 人，硕士生导师 4 人。",-1),l=e("blockquote",null,[e("p",null,"来源: 东北师范大学文学院官网")],-1),r=[s,_,l];function d(i,h){return a(),o("div",null,r)}const f=t(c,[["render",d],["__file","language.html.vue"]]);export{f as default};
