import{_ as n,r as t,o as r,c,a,b as e,d as o,e as l}from"./app-91f40cf0.js";const s={},p=l('<h2 id="长春-吉林城际高速铁路简介" tabindex="-1"><a class="header-anchor" href="#长春-吉林城际高速铁路简介" aria-hidden="true">#</a> 长春-吉林城际高速铁路简介</h2><p>长珲城际铁路龙嘉高铁站位于长春龙嘉国际机场 T1 航站楼正南方向，与机场南侧公路并行，站房形式为地下车站。长珲城铁成为国内第一条实现铁路和空港间零换乘的城际铁路。从城铁下来的乘客可以通过地下通道直接步行抵达航站楼。龙嘉站可乘高铁直达长春火车站、长春西站以及吉林站。</p><p>龙嘉高铁站与长春龙嘉国际机场 T2 航站楼有地下连廊，需要从 1 号通廊向北步行至与 3 号通廊交界处后，一直向东前进即可到达。</p><h3 id="长春站-↔-龙嘉站" tabindex="-1"><a class="header-anchor" href="#长春站-↔-龙嘉站" aria-hidden="true">#</a> 长春站 ↔ 龙嘉站</h3><ul><li><p>长春站至龙嘉站每天 24 车次，历时 14 分钟</p></li><li><p>龙嘉站至长春站每天 22 车次，历时 14 分钟</p></li></ul><h3 id="长春西站-↔-龙嘉站" tabindex="-1"><a class="header-anchor" href="#长春西站-↔-龙嘉站" aria-hidden="true">#</a> 长春西站 ↔ 龙嘉站</h3><ul><li><p>长春西站至龙嘉站往返每天 3 车次，历时 27 分钟</p></li><li><p>龙嘉站至长春西站每日 1 班次，历时 27 分钟</p></li></ul><div class="hint-container info"><p class="hint-container-title">长春-吉林城际高速铁路列票价</p><ul><li><p>长春站 ↔ 龙嘉站: 一等座 15 元，二等座 8.5 元</p></li><li><p>长春西站 ↔ 龙嘉站: 一等座 25 元，二等座 14.5 元</p></li></ul></div><h2 id="列车时刻" tabindex="-1"><a class="header-anchor" href="#列车时刻" aria-hidden="true">#</a> 列车时刻</h2><div class="hint-container warning"><p class="hint-container-title">注意</p><p>以下时刻仅供参考，请以 12306 网站最终安排为准</p></div><figure><img src="https://assets.innenu.com/img/traffic/train-changchun-airport.png" alt="长春-龙嘉-吉林"><figcaption>长春-龙嘉-吉林</figcaption></figure><figure><img src="https://assets.innenu.com/img/traffic/train-airport-changchun.png" alt="吉林-龙嘉-长春"><figcaption>吉林-龙嘉-长春</figcaption></figure>',12),h={href:"http://www.ccairport.cn/Traffic/train",target:"_blank",rel:"noopener noreferrer"};function d(f,u){const i=t("ExternalLinkIcon");return r(),c("div",null,[p,a("blockquote",null,[a("p",null,[e("相关资料来自长春市龙嘉国际机场官网 "),a("a",h,[e("相关链接"),o(i)])])])])}const g=n(s,[["render",d],["__file","train.html.vue"]]);export{g as default};
