import{_ as t,o,c as r,a as e,b as a}from"./app-91f40cf0.js";const c={},s=e("h2",{id:"专业简介",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#专业简介","aria-hidden":"true"},"#"),a(" 专业简介")],-1),n=e("p",null,"比较文学与世界文学是中国语言文学一级学科下设文学类二级学科，历史悠久，基础雄厚，拥有硕士、博士学位点。本学科 1995 年成立“世界文学与比较文学研究中心”，1996 年承办中国比较文学学会年会，2010 年本学科完成校级优质教学团队建设项目，通过省级精品课程评审。2015 年成为吉林省省属高校人文社会科学重点研究基地。承担多项国家社科基金重大、重点、一般、青年项目、教育部项目，获得教育部人文社会科学优秀成果奖等多项奖励；多篇成果入选《中国学术年鉴》。 在古典与中世纪文学研究、北欧与欧洲文学渊源研究、美国文学研究、中日文学比较、比较文学理论等领域方向明确、特色鲜明，成绩显著。如孟庆枢教授在日本当代文学批评、比较文学理论方面的研究广受赞誉，刘建军对拜占庭文学的系统研究在国内外学界具有开创性和填补空白的意义，定期出版有学术年刊《古典与中世纪研究》。",-1),l=e("blockquote",null,[e("p",null,"来源: 东北师范大学文学院官网")],-1),_=[s,n,l];function d(i,h){return o(),r("div",null,_)}const f=t(c,[["render",d],["__file","world-literature.html.vue"]]);export{f as default};
